tables.table_schema:
CREATE TABLE carbon_footprint_data (
  activity_id INT PRIMARY KEY,
  waste_type VARCHAR(50),
  disposal_method VARCHAR(50),
  carbon_emission DECIMAL
);

CREATE TABLE waste_disposal_activities (
    id INT PRIMARY KEY,
    activity_name VARCHAR(50),
    carbon_cost DECIMAL(10, 2),
    disposal_method VARCHAR(50)
);

CREATE TABLE waste_disposal_activities (
    activity_id INT PRIMARY KEY,
    activity_name VARCHAR(255),
    waste_type VARCHAR(100),
    carbon_emission FLOAT
);

CREATE TABLE waste_collection_areas (
    area_id INT PRIMARY KEY,
    area_name VARCHAR(255),
    city VARCHAR(100),
    state VARCHAR(50)
);

CREATE TABLE carbon_emission_factors (
    factor_id INT PRIMARY KEY,
    waste_type VARCHAR(100),
    emission_factor FLOAT
);
