tables.table_schema:
CREATE TABLE WasteManagementFacilities (
  facility_id INT PRIMARY KEY,
  facility_name STRING,
  capacity DECIMAL,
  utilization_rate DECIMAL
);

CREATE TABLE waste_collection (
   collection_id INT PRIMARY KEY,
   bin_id INT REFERENCES waste_bin(bin_id),
   collection_date DATE,
   collection_weight FLOAT
);

CREATE TABLE dashboard_users (
   user_id INT PRIMARY KEY,
   username VARCHAR(50),
   role VARCHAR(50)
);

CREATE TABLE dashboard_logs (
   log_id INT PRIMARY KEY,
   user_id INT REFERENCES dashboard_users(user_id),
   log_date TIMESTAMP,
   action VARCHAR(100)
);
