tables.table_schema:
CREATE TABLE waste_disposal_sites(
  site_id INT PRIMARY KEY,
  site_name VARCHAR,
  location VARCHAR,
  status VARCHAR
);

CREATE TABLE waste_collection_sites (
    site_id SERIAL PRIMARY KEY,
    site_name VARCHAR(255) NOT NULL,
    location VARCHAR(255) NOT NULL,
    waste_type VARCHAR(50) NOT NULL
);

CREATE TABLE waste_collection_vehicles (
    vehicle_id SERIAL PRIMARY KEY,
    vehicle_number VARCHAR(20) NOT NULL,
    vehicle_type VARCHAR(50) NOT NULL
);

CREATE TABLE WasteBins (
    bin_id INT PRIMARY KEY,
    location VARCHAR(255),
    capacity INT,
    current_level INT
);

CREATE TABLE WasteTypes (
    waste_type_id INT PRIMARY KEY,
    waste_name VARCHAR(255),
    description TEXT
);

CREATE TABLE WasteDisposalRoute (
    route_id INT PRIMARY KEY,
    route_name VARCHAR(255),
    start_location VARCHAR(255),
    end_location VARCHAR(255)
);
