tables.table_schema:
CREATE TABLE waste_collection_hotspots (
  location GEOGRAPHY(POINT),
  address VARCHAR(255),
  collection_frequency INT
);

CREATE TABLE interventions (
  location GEOGRAPHY(POLYGON),
  intervention_type VARCHAR(50),
  intervention_cost DECIMAL(10, 2),
  intervention_date DATE
);

CREATE TABLE waste_collection_sites (
    site_id INT PRIMARY KEY,
    site_name VARCHAR(100),
    location GEOGRAPHY,
    collection_frequency INT
);

CREATE TABLE waste_collection_history (
    record_id SERIAL PRIMARY KEY,
    site_id INT REFERENCES waste_collection_sites(site_id),
    collection_date DATE,
    waste_weight DECIMAL
);

CREATE TABLE intervention_recommendations (
    recommendation_id SERIAL PRIMARY KEY,
    site_id INT REFERENCES waste_collection_sites(site_id),
    recommendation_text TEXT
);
