tables.table_schema:
CREATE TABLE waste_management_permits (
permit_id INT PRIMARY KEY,
permit_name VARCHAR(255),
issue_date DATE,
expiration_date DATE
);

CREATE TABLE waste_management_licenses (
license_id INT PRIMARY KEY,
license_name VARCHAR(255),
issue_date DATE,
expiration_date DATE
);

CREATE TABLE PermitHolders (
    holder_id INT PRIMARY KEY,
    holder_name VARCHAR(100),
    contact_email VARCHAR(100),
    contact_phone VARCHAR(20)
);

CREATE TABLE PermitActivities (
    activity_id INT PRIMARY KEY,
    activity_name VARCHAR(100),
    activity_type VARCHAR(50),
    activity_description TEXT
);
