tables.table_schema:
CREATE TABLE smart_home_appliances (
    appliance_id INT PRIMARY KEY,
    appliance_name VARCHAR(255),
    manufacturer VARCHAR(255),
    energy_consumption DECIMAL
);

CREATE TABLE SmartHomeAppliances (
    appliance_id INT PRIMARY KEY,
    appliance_name TEXT,
    power_consumption INT
);

CREATE TABLE ApplianceLocation (
    appliance_id INT,
    location TEXT,
    PRIMARY KEY (appliance_id),
    FOREIGN KEY (appliance_id) REFERENCES SmartHomeAppliances(appliance_id)
);

CREATE TABLE appliances (
    appliance_id SERIAL PRIMARY KEY,
    appliance_name VARCHAR(100) NOT NULL,
    power_consumption DECIMAL NOT NULL
);

CREATE TABLE locations (
    location_id SERIAL PRIMARY KEY,
    location_name VARCHAR(100) NOT NULL
);

CREATE TABLE users (
    user_id SERIAL PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) UNIQUE
);

CREATE TABLE SmartHomeAppliances (
    appliance_id INT PRIMARY KEY,
    appliance_name VARCHAR(255),
    energy_consumption DECIMAL(10,2)
);

CREATE TABLE Rooms (
    room_id INT PRIMARY KEY,
    room_name VARCHAR(255)
);

CREATE TABLE Users (
    user_id INT PRIMARY KEY,
    user_name VARCHAR(255),
    email VARCHAR(255)
);
