tables.table_schema:
CREATE TABLE smart_water_meters (
  meter_id INT PRIMARY KEY,
  location VARCHAR(255),
  reading_date DATE,
  water_usage FLOAT
);

CREATE TABLE WaterMeters (
    meter_id INT PRIMARY KEY,
    location VARCHAR(255),
    household_id INT,
    meter_type VARCHAR(50)
);

CREATE TABLE WaterReadings (
    reading_id INT PRIMARY KEY,
    meter_id INT,
    reading_date DATE,
    usage_gallons INT
);

CREATE TABLE MeterEvents (
    event_id INT PRIMARY KEY,
    meter_id INT,
    event_type VARCHAR(50),
    event_date DATE
);

CREATE TABLE MeterErrors (
    error_id INT PRIMARY KEY,
    meter_id INT,
    error_type VARCHAR(50),
    error_description TEXT
);
