tables.table_schema:
CREATE TABLE traffic_patterns (
    intersection_id INT PRIMARY KEY,
    congestion_level INT,
    traffic_flow INT,
    timestamp TIMESTAMP
);

CREATE TABLE emissions_data (
    vehicle_id INT PRIMARY KEY,
    emission_rate DECIMAL,
    vehicle_type VARCHAR(50),
    timestamp TIMESTAMP
);

CREATE TABLE TrafficPatterns (
    location_id INT PRIMARY KEY,
    location_name VARCHAR(100),
    traffic_volume INT,
    congestion_level VARCHAR(50)
);

CREATE TABLE traffic_patterns (
    timestamp TIMESTAMP NOT NULL,
    location VARCHAR(255) NOT NULL,
    traffic_volume INT NOT NULL,
    congestion_level VARCHAR(50) NOT NULL
);

CREATE TABLE traffic_cameras (
    camera_id INT PRIMARY KEY,
    location VARCHAR(255) NOT NULL,
    camera_type VARCHAR(50) NOT NULL
);

CREATE TABLE transportation_flows (
    timestamp TIMESTAMP NOT NULL,
    route_id INT NOT NULL,
    vehicle_count INT NOT NULL
);

CREATE TABLE emissions_data (
    timestamp TIMESTAMP NOT NULL,
    location VARCHAR(255) NOT NULL,
    greenhouse_gas_emissions FLOAT NOT NULL
);

CREATE TABLE traffic_signals (
    id SERIAL PRIMARY KEY,
    location VARCHAR(100) NOT NULL,
    signal_type VARCHAR(50),
    signal_status VARCHAR(20)
);
