CREATE TABLE pedestrian_accidents (
    accident_id INT PRIMARY KEY,
    accident_date DATE,
    location VARCHAR,
    severity VARCHAR
);

CREATE TABLE cyclist_accidents (
    accident_id INT PRIMARY KEY,
    accident_date DATE,
    location VARCHAR,
    severity VARCHAR
);

CREATE TABLE pedestrian_injuries (
    injury_id INT PRIMARY KEY,
    injury_date DATE,
    location VARCHAR,
    type VARCHAR
);

CREATE TABLE cyclist_injuries (
    injury_id INT PRIMARY KEY,
    injury_date DATE,
    location VARCHAR,
    type VARCHAR
);
