tables.table_schema:
CREATE TABLE Reservoir (
  id INT PRIMARY KEY,
  name VARCHAR,
  capacity FLOAT,
  water_level FLOAT
);

CREATE TABLE River (
  id INT PRIMARY KEY,
  name VARCHAR,
  length FLOAT,
  flow_rate FLOAT
);

CREATE TABLE WaterSensor (
  id INT PRIMARY KEY,
  location VARCHAR,
  type VARCHAR,
  installation_date DATE
);

CREATE TABLE DroughtPrediction (
  id INT PRIMARY KEY,
  location VARCHAR,
  prediction_date DATE,
  severity VARCHAR
);

CREATE TABLE Reservoirs (
  reservoir_id INT PRIMARY KEY,
  reservoir_name VARCHAR(50),
  capacity DECIMAL(10,2),
  current_level DECIMAL(10,2)
);

CREATE TABLE Rivers (
  river_id INT PRIMARY KEY,
  river_name VARCHAR(50),
  length DECIMAL(10,2),
  flow_rate DECIMAL(10,2)
);
