tables.table_schema:
CREATE TABLE EnergyConsumptionData (
  building_id INT PRIMARY KEY,
  date DATE,
  current_energy_consumption DECIMAL
);

CREATE TABLE BuildingInfo (
  building_id INT PRIMARY KEY,
  building_name VARCHAR(255),
  address VARCHAR(255)
);

CREATE TABLE EnergyConsumptionData (
    building_id INT,
    timestamp TIMESTAMP,
    energy_usage DECIMAL,
    PRIMARY KEY (building_id, timestamp)
);

CREATE TABLE BuildingInformation (
    building_id INT PRIMARY KEY,
    building_name VARCHAR(255),
    location VARCHAR(255),
    building_area DECIMAL
);

CREATE TABLE buildings (
    building_id UUID PRIMARY KEY,
    building_name STRING,
    address STRING,
    total_energy_consumption DECIMAL
);

CREATE TABLE floors (
    floor_id UUID PRIMARY KEY,
    building_id UUID REFERENCES buildings(building_id),
    floor_number INT,
    square_footage INT
);

CREATE TABLE meters (
    meter_id UUID PRIMARY KEY,
    building_id UUID REFERENCES buildings(building_id),
    meter_type STRING,
    installation_date DATE
);

CREATE TABLE energy_readings (
    reading_id UUID PRIMARY KEY,
    meter_id UUID REFERENCES meters(meter_id),
    reading_date TIMESTAMP,
    energy_consumed DECIMAL
);

CREATE TABLE Buildings (
    building_id INT PRIMARY KEY,
    building_name VARCHAR(50) NOT NULL,
    address VARCHAR(100) NOT NULL
);

CREATE TABLE Sensors (
    sensor_id INT PRIMARY KEY,
    sensor_type VARCHAR(50) NOT NULL,
    location VARCHAR(100) NOT NULL
);
