tables.table_schema:
CREATE TABLE WaterConsumption (
    id INT PRIMARY KEY,
    location VARCHAR(255),
    usage DECIMAL
);

CREATE TABLE WaterMonitoring (
    id INT PRIMARY KEY,
    location VARCHAR(255),
    reading_date DATE,
    water_level DECIMAL
);

CREATE TABLE WaterConsumption (
    id SERIAL PRIMARY KEY,
    location VARCHAR(255) NOT NULL,
    usage_gallons INT NOT NULL,
    date TIMESTAMP NOT NULL
);

CREATE TABLE WaterUsageHistory (
    id SERIAL PRIMARY KEY,
    location VARCHAR(255) NOT NULL,
    usage_gallons INT NOT NULL,
    date TIMESTAMP NOT NULL
);

CREATE TABLE WaterUsageForecast (
    id SERIAL PRIMARY KEY,
    location VARCHAR(255) NOT NULL,
    forecasted_usage INT NOT NULL,
    forecast_date DATE NOT NULL
);
