tables.table_schema:
CREATE TABLE project (
    project_id INT PRIMARY KEY,
    project_name VARCHAR(255),
    location VARCHAR(255),
    status VARCHAR(50)
);

CREATE TABLE documentation (
    doc_id INT PRIMARY KEY,
    project_id INT,
    document_type VARCHAR(50),
    document_path VARCHAR(255),
    uploaded_date DATE
);

CREATE TABLE Project(id INT PRIMARY KEY, name VARCHAR(255), status VARCHAR(50), certification_level VARCHAR(50));
CREATE TABLE Documentation(id INT PRIMARY KEY, project_id INT, document_type VARCHAR(50), document_description TEXT, document_date DATE);
CREATE TABLE Certification(id INT PRIMARY KEY, project_id INT, certification_date DATE, certification_agent VARCHAR(100));

CREATE TABLE green_building_projects (
    project_id INT PRIMARY KEY,
    project_name VARCHAR(255),
    location VARCHAR(255),
    certification_status VARCHAR(50)
);

CREATE TABLE certification_criteria (
    criteria_id INT PRIMARY KEY,
    criteria_name VARCHAR(255),
    description TEXT
);

CREATE TABLE project_criteria_mapping (
    project_id INT REFERENCES green_building_projects(project_id),
    criteria_id INT REFERENCES certification_criteria(criteria_id),
    completion_status VARCHAR(20),
    PRIMARY KEY (project_id, criteria_id)
);

CREATE TABLE projects (
    project_id INT PRIMARY KEY,
    project_name VARCHAR(255) NOT NULL,
    certification_level VARCHAR(50) NOT NULL
);

CREATE TABLE certification_agencies (
    agency_id INT PRIMARY KEY,
    agency_name VARCHAR(100) NOT NULL
);
