tables.table_schema:
CREATE TABLE historical_energy_data (
    building_id INT,
    date DATE,
    energy_consumption FLOAT,
    temperature FLOAT
);

CREATE TABLE energy_consumption_data (
  building_id INT PRIMARY KEY,
  date DATE,
  energy_consumed FLOAT
);

CREATE TABLE building_energy_info (
  building_id INT PRIMARY KEY,
  building_name VARCHAR,
  energy_rating VARCHAR
);

CREATE TABLE building_energy_data (
  building_id INT PRIMARY KEY,
  energy_usage DECIMAL,
  date DATE
);

CREATE TABLE weather_data (
  date DATE PRIMARY KEY,
  temperature DECIMAL,
  humidity DECIMAL
);

CREATE TABLE building_info (
  building_id INT PRIMARY KEY,
  building_name TEXT,
  location TEXT
);

CREATE TABLE energy_forecast (
  forecast_id INT PRIMARY KEY,
  building_id INT,
  forecast_date DATE,
  forecasted_energy_usage DECIMAL
);

CREATE TABLE buildings (
    building_id INT PRIMARY KEY,
    building_name VARCHAR(100),
    address TEXT,
    construction_year INT
);

CREATE TABLE temperature_data (
    temp_id INT PRIMARY KEY,
    building_id INT,
    temp_value DECIMAL(5,2),
    temp_date DATE
);

CREATE TABLE energy_forecast (
    forecast_id INT PRIMARY KEY,
    building_id INT,
    forecast_date DATE,
    predicted_usage DECIMAL(10,2)
);

CREATE TABLE weather_data (
    weather_id INT PRIMARY KEY,
    building_id INT,
    weather_condition VARCHAR(50),
    weather_date DATE
);
