tables.table_schema:
CREATE TABLE Regulations (
  id UUID PRIMARY KEY,
  regulation_name VARCHAR,
  regulation_description TEXT
);

CREATE TABLE environmental_regulations (
  regulation_id INT PRIMARY KEY,
  regulation_name VARCHAR(100) NOT NULL,
  description TEXT
);

CREATE TABLE building_compliance (
  building_id INT PRIMARY KEY,
  regulation_id INT REFERENCES environmental_regulations(regulation_id),
  compliance_status BOOLEAN
);

CREATE TABLE disposal_type (
  type_id INT PRIMARY KEY,
  type_name VARCHAR(50),
  description TEXT
);

CREATE TABLE disposal_personnel (
  personnel_id INT PRIMARY KEY,
  personnel_name VARCHAR(50),
  personnel_role ENUM('Manager', 'Supervisor', 'Worker')
);
