tables.table_schema:
CREATE TABLE energy_consumption_data (
    household_id UUID PRIMARY KEY,
    timestamp timestamp,
    energy_usage DECIMAL
);

CREATE TABLE household_info (
    household_id UUID PRIMARY KEY,
    address VARCHAR,
    family_size INT
);

CREATE TABLE energy_consumption_data (
    household_id INT PRIMARY KEY,
    date DATE,
    time TIME,
    energy_usage DECIMAL
);

CREATE TABLE energy_consumption_patterns (
    household_id INT,
    pattern_type VARCHAR,
    pattern_description TEXT,
    PRIMARY KEY (household_id, pattern_type)
);

CREATE TABLE households (
    household_id INT PRIMARY KEY,
    location VARCHAR(50) NOT NULL,
    energy_provider VARCHAR(50) NOT NULL
);

CREATE TABLE energy_readings (
    reading_id INT PRIMARY KEY,
    household_id INT NOT NULL,
    reading_value INT NOT NULL,
    reading_date TIMESTAMP NOT NULL
);

CREATE TABLE usage_patterns (
    pattern_id INT PRIMARY KEY,
    household_id INT NOT NULL,
    pattern_description VARCHAR(255) NOT NULL
);

CREATE TABLE usage_optimization (
    optimization_id INT PRIMARY KEY,
    household_id INT NOT NULL,
    optimization_strategy VARCHAR(255) NOT NULL
);
