tables.table_schema:
CREATE TABLE energy_usage (
    facility_id INT PRIMARY KEY,
    facility_name VARCHAR(50),
    energy_value FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE energy_facilities (
    facility_id INT PRIMARY KEY,
    facility_name VARCHAR(50) NOT NULL,
    energy_usage DECIMAL NOT NULL
);

CREATE TABLE energy_data (
    data_id INT PRIMARY KEY,
    facility_id INT REFERENCES energy_facilities(facility_id),
    timestamp TIMESTAMP NOT NULL,
    usage DECIMAL NOT NULL
);

CREATE TABLE energy_facilities (
    facility_id UUID PRIMARY KEY,
    facility_name STRING,
    location STRING
);

CREATE TABLE energy_metering (
    meter_id UUID PRIMARY KEY,
    facility_id UUID,
    timestamp TIMESTAMP,
    energy_usage FLOAT
);

CREATE TABLE energy_users (
    user_id UUID PRIMARY KEY,
    username STRING,
    email STRING,
    facility_id UUID
);

CREATE TABLE facility (
 id INT PRIMARY KEY,
 name VARCHAR
);

CREATE TABLE building (
 id INT PRIMARY KEY,
 facility_id INT REFERENCES facility(id),
 name VARCHAR
);

CREATE TABLE room (
 id INT PRIMARY KEY,
 building_id INT REFERENCES building(id),
 name VARCHAR
);

CREATE TABLE energy_usage (
 id INT PRIMARY KEY,
 room_id INT REFERENCES room(id),
 timestamp TIMESTAMP,
 energy_usage FLOAT
);
