tables.table_schema:
CREATE TABLE building_occupancy_data (
   building_id UUID PRIMARY KEY,
   occupancy_status BOOLEAN,
   occupancy_start_time TIMESTAMP,
   occupancy_end_time TIMESTAMP
);

CREATE TABLE Building (
    building_id INT PRIMARY KEY,
    building_name VARCHAR(255) NOT NULL,
    building_address VARCHAR(255) NOT NULL
);

CREATE TABLE OccupancyData (
    occupancy_id INT PRIMARY KEY,
    building_id INT,
    occupancy_date DATE NOT NULL,
    occupancy_hours INT NOT NULL
);

CREATE TABLE occupancy_data (
    building_id INT,
    date DATE,
    time TIME,
    occupancy_status BOOLEAN
);

CREATE TABLE building_info (
    building_id INT,
    building_name VARCHAR(255),
    address VARCHAR(255),
    floor_area FLOAT
);

CREATE TABLE Building (
    building_id INT PRIMARY KEY,
    building_name STRING,
    building_type STRING
);

CREATE TABLE OccupancyData (
    occupancy_id INT PRIMARY KEY,
    building_id INT,
    occupancy_date DATE,
    occupancy_level INT
);

CREATE TABLE HeatingSchedule (
    schedule_id INT PRIMARY KEY,
    occupancy_id INT,
    heating_start_time TIME,
    heating_end_time TIME
);

CREATE TABLE CoolingSchedule (
    schedule_id INT PRIMARY KEY,
    occupancy_id INT,
    cooling_start_time TIME,
    cooling_end_time TIME
);

CREATE TABLE TemperatureSensors (
    sensor_id INT PRIMARY KEY,
    building_id INT,
    sensor_location STRING,
    sensor_type STRING
);
