tables.table_schema:
CREATE TABLE energy_efficiency_tracking (
  process_id INT PRIMARY KEY,
  process_name VARCHAR(50) NOT NULL,
  energy_saved DECIMAL(10,2) NOT NULL,
  cost_savings DECIMAL(10,2) NOT NULL
);

CREATE TABLE energy_efficiency_improvements (
 id INT PRIMARY KEY,
 process_name VARCHAR(255),
 improvement_type VARCHAR(255),
 cost_savings DECIMAL
);

CREATE TABLE industrial_processes (
 id INT PRIMARY KEY,
 process_name VARCHAR(255),
 energy_consumption DECIMAL,
 efficiency_rating INT
);

CREATE TABLE energy_sources (
    id INT PRIMARY KEY,
    source_name VARCHAR(255) NOT NULL,
    source_type VARCHAR(50) NOT NULL
);

CREATE TABLE energy_consumption (
    id INT PRIMARY KEY,
    source_id INT REFERENCES energy_sources(id),
    consumption_date DATE NOT NULL,
    energy_consumed DECIMAL(10, 2) NOT NULL
);

CREATE TABLE cost_savings (
    id INT PRIMARY KEY,
    source_id INT REFERENCES energy_sources(id),
    saving_date DATE NOT NULL,
    cost_savings DECIMAL(10, 2) NOT NULL
);

CREATE TABLE process_improvements (
    id INT PRIMARY KEY,
    source_id INT REFERENCES energy_sources(id),
    improvement_date DATE NOT NULL,
    improvement_description TEXT
);

CREATE TABLE EnergyConsumption (
    plant_id INT,
    month DATE,
    energy_usage DECIMAL,
    cost DECIMAL
);

CREATE TABLE EnergySavings (
    plant_id INT,
    month DATE,
    savings DECIMAL,
    source VARCHAR(100)
);

CREATE TABLE ProcessImprovements (
    plant_id INT,
    process_id INT,
    improvement_area VARCHAR(100),
    cost_saved DECIMAL
);

CREATE TABLE Plants (
    plant_id INT PRIMARY KEY,
    plant_name VARCHAR(100),
    location VARCHAR(100)
);

CREATE TABLE Processes (
    process_id INT PRIMARY KEY,
    process_name VARCHAR(100),
    description TEXT
);
