tables.table_schema:
CREATE TABLE energy_sources (
    source_id INT PRIMARY KEY,
    source_name VARCHAR,
    source_type VARCHAR,
    location VARCHAR
);

CREATE TABLE energy_consumption (
    consumption_id INT PRIMARY KEY,
    source_id INT,
    date DATE,
    energy_consumed DECIMAL,
    FOREIGN KEY (source_id) REFERENCES energy_sources(source_id)
);

CREATE TABLE energy_consumption_data (
    source_id INT PRIMARY KEY,
    source_name TEXT,
    consumption_value FLOAT
);

CREATE TABLE energy_sources (
    source_id INT PRIMARY KEY,
    source_name TEXT,
    location TEXT
);

CREATE TABLE energy_reports (
    report_id INT PRIMARY KEY,
    report_name TEXT,
    report_date DATE,
    total_consumption FLOAT
);

CREATE TABLE energy_consumption_data (
  id INT PRIMARY KEY,
  source_id INT,
  timestamp TIMESTAMP,
  consumption_value DECIMAL
);

CREATE TABLE energy_sources (
  source_id INT PRIMARY KEY,
  source_name VARCHAR(50),
  region VARCHAR(50),
  capacity DECIMAL
);

CREATE TABLE reports (
  report_id INT PRIMARY KEY,
  report_name VARCHAR(100),
  report_date DATE,
  report_content TEXT
);

CREATE TABLE insights (
  insight_id INT PRIMARY KEY,
  insight_description VARCHAR(200),
  insight_date DATE
);
