tables.table_schema:
CREATE TABLE water_saving_tips (
  tip_id INT PRIMARY KEY,
  tip_category VARCHAR(50),
  tip_description TEXT,
  tip_author VARCHAR(100)
);

CREATE TABLE WaterSavingTipsHomeowners (
    id INT PRIMARY KEY,
    title VARCHAR(255) NOT NULL,
    description TEXT NOT NULL
);

CREATE TABLE WaterSavingTipsBusinesses (
    id INT PRIMARY KEY,
    title VARCHAR(255) NOT NULL,
    description TEXT NOT NULL,
    category VARCHAR(50) NOT NULL
);

CREATE TABLE WaterSavingTipsForHomeowners (
    tip_id INT PRIMARY KEY,
    tip_title VARCHAR(100),
    tip_description TEXT
);

CREATE TABLE WaterSavingTipsForBusinesses (
    tip_id INT PRIMARY KEY,
    tip_title VARCHAR(100),
    tip_description TEXT,
    business_category VARCHAR(50)
);

CREATE TABLE UserSubmissions (
    submission_id INT PRIMARY KEY,
    user_name VARCHAR(50),
    submission_text TEXT,
    submission_date TIMESTAMP
);

CREATE TABLE Categories (
    id SERIAL PRIMARY KEY,
    category_name VARCHAR(50) UNIQUE NOT NULL
);

CREATE TABLE Users (
    id SERIAL PRIMARY KEY,
    username VARCHAR(50) UNIQUE NOT NULL,
    email VARCHAR(100) UNIQUE NOT NULL,
    password_hash VARCHAR(100) NOT NULL
);

CREATE TABLE Likes (
    id SERIAL PRIMARY KEY,
    user_id INTEGER REFERENCES Users(id),
    tip_id INTEGER REFERENCES WaterSavingTips(id)
);

CREATE TABLE WaterSavingTips (
	tip_id INT PRIMARY KEY,
	tip_title VARCHAR(255),
	tip_description TEXT
);

CREATE TABLE Users (
	user_id INT PRIMARY KEY,
	username VARCHAR(50),
	email VARCHAR(100),
	is_homeowner BOOLEAN
);

CREATE TABLE Categories (
	category_id INT PRIMARY KEY,
