tables.table_schema:
CREATE TABLE zoning_regulations (
    neighborhood_id INT PRIMARY KEY,
    regulations TEXT
);

CREATE TABLE neighborhoods (
    neighborhood_id INT PRIMARY KEY,
    neighborhood_name VARCHAR(50)
);

CREATE TABLE neighborhood (neighborhood_id INT, name VARCHAR(255), location VARCHAR(255));

CREATE TABLE zoning_regulations (regulation_id INT, neighborhood_id INT, regulation_text TEXT, regulation_type VARCHAR(50));

CREATE TABLE violation_records (violation_id INT, regulation_id INT, description TEXT, date_recorded DATE);

CREATE TABLE neighborhood (
    neighborhood_id INT PRIMARY KEY,
    neighborhood_name VARCHAR(50) NOT NULL,
    city VARCHAR(50) NOT NULL
);

CREATE TABLE zoning_category (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(50) NOT NULL,
    description VARCHAR(255)
);

CREATE TABLE zoning_regulations (
    regulation_id INT PRIMARY KEY,
    category_id INT REFERENCES zoning_category(category_id),
    neighborhood_id INT REFERENCES neighborhood(neighborhood_id),
    regulation_description VARCHAR(255) NOT NULL
);

CREATE TABLE zone_updates (
    update_id INT PRIMARY KEY,
    regulation_id INT REFERENCES zoning_regulations(regulation_id),
    update_date DATE NOT NULL,
    update_description VARCHAR(255)
);

CREATE TABLE neighborhoods (
    neighborhood_id INT PRIMARY KEY,
    neighborhood_name VARCHAR(50)
);

CREATE TABLE zoning_categories (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(50)
);

CREATE TABLE compliance_records (
    record_id INT PRIMARY KEY,
    compliance_status VARCHAR(50),
    regulation_id INT,
    FOREIGN KEY (regulation_id) REFERENCES regulations(regulation_id)
);

CREATE TABLE violations (
    violation_id INT PRIMARY KEY,
    violation_description TEXT,
    record_id INT,
    FOREIGN KEY (record_id) REFERENCES compliance_records(record_id)
);
