tables.table_schema:
CREATE TABLE routes (
  route_id INT PRIMARY KEY,
  route_name VARCHAR(100) NOT NULL,
  start_location VARCHAR(100) NOT NULL,
  end_location VARCHAR(100) NOT NULL
);

CREATE TABLE public_transport_routes (
  route_id INT PRIMARY KEY,
  route_name VARCHAR(100) NOT NULL,
  start_point VARCHAR(100) NOT NULL,
  end_point VARCHAR(100) NOT NULL
);

CREATE TABLE public_transport_schedules (
  schedule_id INT PRIMARY KEY,
  route_id INT REFERENCES public_transport_routes(route_id),
  departure_time TIME NOT NULL,
  arrival_time TIME NOT NULL
);

CREATE TABLE public_transport_stops (
  stop_id INT PRIMARY KEY,
  stop_name VARCHAR(100) NOT NULL,
  stop_location VARCHAR(100) NOT NULL
);

CREATE TABLE routes (
  route_id INT PRIMARY KEY,
  route_name VARCHAR(50) NOT NULL,
  start_location VARCHAR(50) NOT NULL,
  end_location VARCHAR(50) NOT NULL
);

CREATE TABLE stops (
  stop_id INT PRIMARY KEY,
  stop_name VARCHAR(50) NOT NULL,
  stop_location VARCHAR(50) NOT NULL
);

CREATE TABLE vehicles (
  vehicle_id INT PRIMARY KEY,
  vehicle_type VARCHAR(50) NOT NULL,
  capacity INT NOT NULL
);

CREATE TABLE drivers (
  driver_id INT PRIMARY KEY,
  driver_name VARCHAR(50) NOT NULL,
  driver_license_number VARCHAR(20) NOT NULL
);
