tables.table_schema:
CREATE TABLE land_use (
   id INT PRIMARY KEY,
   area_name VARCHAR(50),
   land_type VARCHAR(50),
   land_area FLOAT
);

CREATE TABLE infrastructure (
   id INT PRIMARY KEY,
   location_name VARCHAR(50),
   infrastructure_type VARCHAR(50),
   construction_date DATE
);

CREATE TABLE land_use (
  land_use_id SERIAL PRIMARY KEY,
  area_name VARCHAR(100),
  land_type VARCHAR(50),
  land_area FLOAT
);

CREATE TABLE infrastructure (
  infrastructure_id SERIAL PRIMARY KEY,
  infrastructure_name VARCHAR(100),
  infrastructure_type VARCHAR(50),
  location GEOGRAPHY
);

CREATE TABLE development_progress (
  progress_id SERIAL PRIMARY KEY,
  project_id INT REFERENCES urban_development(project_id),
  stage_name VARCHAR(100),
  completion_date DATE
);

CREATE TABLE geospatial_data (
    id SERIAL PRIMARY KEY,
    location POINT,
    land_use_type VARCHAR(50),
    infrastructure VARCHAR(50)
);

CREATE TABLE urban_projects (
    project_id SERIAL PRIMARY KEY,
    project_name VARCHAR(100),
    start_date DATE,
    completion_date DATE
);

CREATE TABLE land_use_analysis (
    analysis_id SERIAL PRIMARY KEY,
    land_area FLOAT,
    land_use_type VARCHAR(50),
    project_id INT
);

CREATE TABLE infrastructure_data (
    infrastructure_id SERIAL PRIMARY KEY,
    infrastructure_type VARCHAR(50),
    location POINT,
    installation_date DATE
);

CREATE TABLE development_history (
    history_id SERIAL PRIMARY KEY,
    project_id INT,
    land_use_type VARCHAR(50),
    completion_status BOOLEAN
);
