tables.table_schema:
CREATE TABLE historical_landmarks (
    landmark_id INT PRIMARY KEY,
    landmark_name STRING,
    location STRING,
    year_of_establishment INT
);

CREATE TABLE Landmarks (
  landmark_id INT PRIMARY KEY,
  landmark_name TEXT NOT NULL,
  location TEXT NOT NULL,
  year_established INT
);

CREATE TABLE PreservationSites (
  preservation_site_id INT PRIMARY KEY,
  site_name TEXT NOT NULL,
  location TEXT NOT NULL,
  preservation_area FLOAT
);

CREATE TABLE LandmarkDetails (
  detail_id INT PRIMARY KEY,
  landmark_id INT REFERENCES Landmarks(landmark_id),
  description TEXT
);

CREATE TABLE SiteDetails (
  detail_id INT PRIMARY KEY,
  preservation_site_id INT REFERENCES PreservationSites(preservation_site_id),
  description TEXT
);

CREATE TABLE landmarks (
    id INT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    location VARCHAR(255) NOT NULL,
    year_established INT
);

CREATE TABLE historical_events (
    id INT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    date DATE,
    description TEXT
);

CREATE TABLE historical_figures (
    id INT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    birth_year INT,
    death_year INT
);
