tables.table_schema:
CREATE TABLE crime_reports (
    report_id INT PRIMARY KEY,
    report_date DATE,
    crime_type VARCHAR(255),
    location VARCHAR(255)
);

CREATE TABLE crime_hotspots (
    hotspot_id INT PRIMARY KEY,
    location_name VARCHAR(255),
    crime_rate DECIMAL,
    last_updated TIMESTAMP
);

CREATE TABLE crime_hotspots (
    hotspot_id INT PRIMARY KEY,
    location VARCHAR(100),
    crime_level INT,
    patrol_required BOOLEAN
);

CREATE TABLE patrol_officers (
    officer_id INT PRIMARY KEY,
    officer_name VARCHAR(50),
    precinct VARCHAR(50),
    shift VARCHAR(20)
);

CREATE TABLE crimes (
    crime_id INT PRIMARY KEY,
    crime_type VARCHAR(50),
    crime_date DATE,
    crime_location VARCHAR(100)
);

CREATE TABLE crime_hotspots (
    hotspot_id INT PRIMARY KEY,
    location_name VARCHAR(100),
    hotspot_type VARCHAR(50),
    reported_crime_count INT
);

CREATE TABLE crime_strategies (
    strategy_id INT PRIMARY KEY,
    strategy_name VARCHAR(100),
    related_hotspot_id INT,
    FOREIGN KEY (related_hotspot_id) REFERENCES crime_hotspots(hotspot_id)
);

CREATE TABLE crimes (
    id SERIAL PRIMARY KEY,
    crime_type VARCHAR(50) NOT NULL,
    crime_date DATE NOT NULL,
    location POINT NOT NULL
);

CREATE TABLE crime_locations (
    id SERIAL PRIMARY KEY,
    location_name VARCHAR(100) NOT NULL,
    latitude DECIMAL(9, 6) NOT NULL,
    longitude DECIMAL(9, 6) NOT NULL
);

CREATE TABLE crime_reports (
    id SERIAL PRIMARY KEY,
    report_date DATE NOT NULL,
    crime_id INT NOT NULL,
    location_id INT NOT NULL
);

CREATE TABLE crime_analysis (
    id SERIAL PRIMARY KEY,
    analysis_date DATE NOT NULL,
    crime_id INT NOT NULL,
    analysis_results JSONB
);

CREATE TABLE crime_strategies (
    id SERIAL PRIMARY KEY,
    strategy_name VARCHAR(100) NOT NULL,
    strategy_description TEXT
);
