tables.table_schema:
CREATE TABLE AirQualityData (
  data_id UUID PRIMARY KEY,
  location_id UUID,
  air_quality_index INT,
  timestamp TIMESTAMP
);

CREATE TABLE PollutionData (
  data_id UUID PRIMARY KEY,
  location_id UUID,
  pollution_level FLOAT,
  timestamp TIMESTAMP
);

CREATE TABLE air_quality_data (
    id INT PRIMARY KEY,
    area_name VARCHAR(50),
    pollution_level INT,
    air_quality_index INT
);

CREATE TABLE temperature_data (
    id INT PRIMARY KEY,
    area_name VARCHAR(50),
    temperature FLOAT,
    humidity FLOAT
);

CREATE TABLE pollution_sources (
    id INT PRIMARY KEY,
    source_name VARCHAR(50),
    pollution_type VARCHAR(50),
    pollution_level INT
);

CREATE TABLE city_areas (
    id INT PRIMARY KEY,
    area_name VARCHAR(50),
    population INT,
    geographical_location VARCHAR(100)
);
