tables.table_schema:
CREATE TABLE public_parks (
	park_id INT PRIMARY KEY,
	park_name VARCHAR(255) NOT NULL,
	location VARCHAR(255) NOT NULL
);

CREATE TABLE park_amenities (
	park_id INT,
	amenity_id INT PRIMARY KEY,
	amenity_name VARCHAR(255) NOT NULL,
	amenity_type VARCHAR(50) NOT NULL
);

CREATE TABLE visitor_statistics (
	visitor_id INT PRIMARY KEY,
	park_id INT,
	visits INT NOT NULL,
	visitor_type VARCHAR(50) NOT NULL
);

CREATE TABLE public_parks (id UUID PRIMARY KEY,
  park_name VARCHAR(100) NOT NULL,
  location VARCHAR(255) NOT NULL,
  established_date DATE
);

CREATE TABLE park_amenities (id UUID PRIMARY KEY,
  park_id UUID REFERENCES public_parks(id),
  amenity_name VARCHAR(100) NOT NULL,
  category VARCHAR(50)
);

CREATE TABLE visitor_statistics (id UUID PRIMARY KEY,
  park_id UUID REFERENCES public_parks(id),
  visitor_count INT,
  visit_date DATE
);

CREATE TABLE park_planning (id UUID PRIMARY KEY,
  park_id UUID REFERENCES public_parks(id),
  current_projects TEXT,
  future_projects TEXT
);
