tables.table_schema:
CREATE TABLE HousingData (
    city_name STRING,
    zip_code INT,
    median_income INT,
    median_rent INT
);

CREATE TABLE housing_affordability (
  city_id INT PRIMARY KEY,
  city_name VARCHAR(255),
  median_income INT,
  median_rent DECIMAL
);

CREATE TABLE rental_prices (
  property_id INT PRIMARY KEY,
  property_name VARCHAR(255),
  property_type VARCHAR(50),
  rent_price DECIMAL
);
