tables.table_schema:
CREATE TABLE buses (
  bus_id INT PRIMARY KEY,
  route_id INT,
  arrival_time TIMESTAMP,
  departure_time TIMESTAMP
);

CREATE TABLE trains (
  train_id INT PRIMARY KEY,
  route_id INT,
  arrival_time TIMESTAMP,
  departure_time TIMESTAMP
);

CREATE TABLE BusLocation (
    bus_id INT PRIMARY KEY,
    latitude DECIMAL,
    longitude DECIMAL,
    timestamp TIMESTAMP
);

CREATE TABLE TrainLocation (
    train_id INT PRIMARY KEY,
    latitude DECIMAL,
    longitude DECIMAL,
    timestamp TIMESTAMP
);

CREATE TABLE Schedule (
    schedule_id INT PRIMARY KEY,
    vehicle_id INT,
    arrival_time TIMESTAMP,
    departure_time TIMESTAMP
);

CREATE TABLE BusLocations (
    bus_id INT PRIMARY KEY,
    latitude FLOAT,
    longitude FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE TrainLocations (
    train_id INT PRIMARY KEY,
    latitude FLOAT,
    longitude FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE BusStatus (
    bus_id INT PRIMARY KEY,
    status VARCHAR(50),
    last_updated TIMESTAMP
);

CREATE TABLE TrainStatus (
    train_id INT PRIMARY KEY,
    status VARCHAR(50),
    last_updated TIMESTAMP
);

CREATE TABLE bus_routes (
  route_id INT PRIMARY KEY,
  route_name VARCHAR(255),
  starting_point VARCHAR(255),
  ending_point VARCHAR(255)
);

CREATE TABLE train_routes (
  route_id INT PRIMARY KEY,
  route_name VARCHAR(255),
  starting_point VARCHAR(255),
  ending_point VARCHAR(255)
);
