tables.table_schema:
CREATE TABLE customer_feedback (
  feedback_id INT PRIMARY KEY,
  customer_name VARCHAR(50),
  product_id INT,
  feedback_text TEXT
);

CREATE TABLE green_products (
  product_id INT PRIMARY KEY,
  product_name VARCHAR(50),
  product_category VARCHAR(50),
  product_rating DECIMAL(2,1)
);

CREATE TABLE product_offerings (
  product_id INT PRIMARY KEY,
  offering_id INT,
  offering_name VARCHAR(50),
  offering_discount DECIMAL(4,2)
);

CREATE TABLE Customers (
  customer_id INT PRIMARY KEY,
  customer_name VARCHAR(50),
  customer_age INT,
  customer_email VARCHAR(100)
);

CREATE TABLE Products (
  product_id INT PRIMARY KEY,
  product_name VARCHAR(50),
  product_category VARCHAR(50),
  product_price DECIMAL(10, 2)
);

CREATE TABLE Categories (
  category_id INT PRIMARY KEY,
  category_name VARCHAR(50)
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(50),
    customer_email VARCHAR(50)
);

CREATE TABLE products (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(50),
    product_category VARCHAR(50)
);

CREATE TABLE product_feedback (
    product_id INT,
    feedback_id INT,
    CONSTRAINT fk_product_feedback_product_id FOREIGN KEY (product_id) REFERENCES products(product_id),
    CONSTRAINT fk_product_feedback_feedback_id FOREIGN KEY (feedback_id) REFERENCES customer_feedback(feedback_id)
);

CREATE TABLE product_ratings (
    product_id INT PRIMARY KEY,
    overall_rating FLOAT,
    quality_rating FLOAT,
    performance_rating FLOAT
);
