tables.table_schema:
CREATE TABLE emissions (
  id UUID PRIMARY KEY,
  date DATE,
  amount DECIMAL,
  location VARCHAR(255)
);

CREATE TABLE reduction_efforts (
  id UUID PRIMARY KEY,
  date DATE,
  method VARCHAR(255),
  cost DECIMAL
);

CREATE TABLE emissions (
   id UUID PRIMARY KEY,
   date DATE,
   location VARCHAR(255),
   emissions FLOAT
);

CREATE TABLE activities (
   id UUID PRIMARY KEY,
   activity_name VARCHAR(255),
   activity_description TEXT,
   emissions_saved FLOAT
);

CREATE TABLE users (
   id UUID PRIMARY KEY,
   username VARCHAR(255),
   email VARCHAR(255),
   total_emissions_saved FLOAT
);
