    brand TEXT NOT NULL,
    appliance_type TEXT NOT NULL,
    energy_consumption FLOAT NOT NULL
);

CREATE TABLE appliance_categories (
    category_id SERIAL PRIMARY KEY,
    category_name TEXT NOT NULL
);

CREATE TABLE eco_friendly_appliances (
    appliance_id UUID PRIMARY KEY,
    appliance_name VARCHAR(255) NOT NULL,
    energy_consumption DECIMAL NOT NULL,
    eco_rating INT
);

CREATE TABLE energy_reports (
    report_id SERIAL PRIMARY KEY,
    report_date DATE NOT NULL,
    appliance_id UUID REFERENCES eco_friendly_appliances(appliance_id) ON DELETE CASCADE,
    energy_usage DECIMAL NOT NULL
);

CREATE TABLE appliance_categories (
    category_id SERIAL PRIMARY KEY,
    category_name VARCHAR(50) NOT NULL
);

CREATE TABLE appliance_category_mapping (
    mapping_id SERIAL PRIMARY KEY,
    appliance_id UUID REFERENCES eco_friendly_appliances(appliance_id) ON DELETE CASCADE,
    category_id INT REFERENCES appliance_categories(category_id) ON DELETE CASCADE
);

CREATE TABLE EnergyReports (
	report_id UUID PRIMARY KEY,
	appliance_name VARCHAR(255),
	energy_consumption DECIMAL,
	timestamp TIMESTAMP
);

CREATE TABLE Appliances (
	appliance_id UUID PRIMARY KEY,
	appliance_name VARCHAR(255),
	energy_rating DECIMAL
);

CREATE TABLE Categories (
	category_id UUID PRIMARY KEY,
	category_name VARCHAR(255)
);

CREATE TABLE ApplianceCategory (
	id UUID PRIMARY KEY,
	appliance_id UUID,
	category_id UUID
);

CREATE TABLE Users (
	user_id UUID PRIMARY KEY,
	username VARCHAR(255),
	email VARCHAR(255)
);
