CREATE TABLE loyalty_points (
	loyalty_id INT PRIMARY KEY,
	customer_id INT,
	points INT DEFAULT 0,
	FOREIGN KEY (customer_id) REFERENCES customers(customer_id)
);

CREATE TABLE eco_friendly_items (
