CREATE TABLE production (
  product_id INT PRIMARY KEY,
  product_name VARCHAR(255),
  production_date DATE,
  production_location VARCHAR(255)
);

CREATE TABLE Production (
    product_id UUID PRIMARY KEY,
    product_name VARCHAR(255),
    production_date DATE,
    production_location VARCHAR(100)
);

CREATE TABLE Inventory (
    inventory_id UUID PRIMARY KEY,
    product_id UUID REFERENCES Production(product_id),
    quantity INTEGER,
    expiration_date DATE
);

CREATE TABLE Disposal (
    disposal_id UUID PRIMARY KEY,
    inventory_id UUID REFERENCES Inventory(inventory_id),
    disposal_date DATE,
    disposal_location VARCHAR(100)
);

CREATE TABLE Production (
    product_id VARCHAR(50) PRIMARY KEY,
    product_name VARCHAR(100),
    production_date DATE,
    production_location VARCHAR(100)
);

CREATE TABLE Distribution (
    distribution_id VARCHAR(50) PRIMARY KEY,
    product_id VARCHAR(50) REFERENCES Production(product_id),
    distributor_name VARCHAR(100),
    distribution_date DATE
);

CREATE TABLE Consumption (
    consumption_id VARCHAR(50) PRIMARY KEY,
    product_id VARCHAR(50) REFERENCES Production(product_id),
    consumer_name VARCHAR(100),
    consumption_date DATE
);

CREATE TABLE Disposal (
    disposal_id VARCHAR(50) PRIMARY KEY,
    product_id VARCHAR(50) REFERENCES Production(product_id),
    disposal_method VARCHAR(100),
    disposal_date DATE
);
