tables.table_schema:
CREATE TABLE eco_friendly_items (
    item_id INT PRIMARY KEY,
    item_name VARCHAR(255) NOT NULL,
    sustainability_standard VARCHAR(100) NOT NULL,
    certification_status BOOLEAN
);

CREATE TABLE sustainability_standards (
    id INT PRIMARY KEY,
    item_name VARCHAR(255),
    eco_certification VARCHAR(50),
    compliance_check BOOLEAN
);

CREATE TABLE compliance_checks (
    check_id INT PRIMARY KEY,
    date_checked DATE,
    outcome VARCHAR(50),
    remarks TEXT
);

CREATE TABLE eco_items (
    item_id INT PRIMARY KEY,
    item_name VARCHAR(255),
    eco_certification VARCHAR(50),
    compliance_check BOOLEAN
);

CREATE TABLE sustainability_standards (
    standard_id INT PRIMARY KEY,
    standard_name VARCHAR(100),
    description TEXT
);

CREATE TABLE compliance_checks (
    check_id INT PRIMARY KEY,
    item_id INT REFERENCES eco_items(item_id),
    standard_id INT REFERENCES sustainability_standards(standard_id),
    passed BOOLEAN
);

CREATE TABLE sustainability_standards (
    id INT PRIMARY KEY,
    item_name VARCHAR(255) NOT NULL,
    certification VARCHAR(50) NOT NULL,
    compliance_check BOOLEAN
);

CREATE TABLE items (
    id INT PRIMARY KEY,
    item_name VARCHAR(255) NOT NULL,
    price DECIMAL(10,2) NOT NULL,
    category VARCHAR(50) NOT NULL
);

CREATE TABLE certifications (
    id INT PRIMARY KEY,
    certification_name VARCHAR(50) NOT NULL,
    issuing_organization VARCHAR(100) NOT NULL,
    validity_period INT NOT NULL
);

CREATE TABLE compliance_checks (
    id INT PRIMARY KEY,
    check_name VARCHAR(50) NOT NULL,
    check_description TEXT,
    pass_criteria VARCHAR(100) NOT NULL
);
