tables.table_schema:
CREATE TABLE customer_interactions (
  customer_id INT,
  touchpoint_id INT,
  interaction_date DATE,
  interaction_type VARCHAR(50)
);

CREATE TABLE customer_touchpoints (
    customer_id INT PRIMARY KEY,
    touchpoint_id INT,
    touchpoint_type VARCHAR(50),
    interaction_datetime TIMESTAMP
);

CREATE TABLE touchpoints (
    touchpoint_id INT PRIMARY KEY,
    touchpoint_name VARCHAR(50) NOT NULL,
    touchpoint_channel VARCHAR(50) NOT NULL
);
