tables.table_schema:
CREATE TABLE surveys (
    survey_id INT PRIMARY KEY,
    customer_id INT,
    survey_date DATE,
    satisfaction_score INT
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(50),
    total_purchases INT,
    loyalty_level INT
);

CREATE TABLE customer_survey (
    survey_id SERIAL PRIMARY KEY,
    customer_name VARCHAR(100),
    satisfaction_score INT,
    survey_timestamp TIMESTAMP
);

CREATE TABLE survey_questions (
    question_id SERIAL PRIMARY KEY,
    question_text VARCHAR(255)
);

CREATE TABLE Customer ( 
	customer_id INT PRIMARY KEY, 
	customer_name VARCHAR(50) NOT NULL, 
	customer_email VARCHAR(100) UNIQUE, 
	customer_phone VARCHAR(15) 
);

CREATE TABLE Survey ( 
	survey_id INT PRIMARY KEY, 
	survey_name VARCHAR(50) NOT NULL, 
	survey_description TEXT 
);

CREATE TABLE Question ( 
	question_id INT PRIMARY KEY, 
	question_text TEXT NOT NULL, 
	survey_id INT REFERENCES Survey(survey_id) 
);

CREATE TABLE Response ( 
	response_id INT PRIMARY KEY, 
	response_text TEXT NOT NULL, 
	question_id INT REFERENCES Question(question_id), 
	customer_id INT REFERENCES Customer(customer_id) 
);

CREATE TABLE SatisfactionScore ( 
	score_id INT PRIMARY KEY, 
	score_value INT NOT NULL, 
	response_id INT REFERENCES Response(response_id) 
);
