tables.table_schema:
CREATE TABLE product_demand_forecast (
  product_id INT PRIMARY KEY,
  product_name VARCHAR(255),
  historical_sales_data JSONB,
  market_trends_data JSONB
);

CREATE TABLE Sales ( 
    product_id INT, 
    date DATE, 
    sales_amount DECIMAL(10, 2) 
);

CREATE TABLE MarketTrends ( 
    trend_id INT, 
    date DATE, 
    trend_description TEXT 
);

CREATE TABLE Product (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(255),
    category VARCHAR(50),
    unit_price DECIMAL(10, 2)
);

CREATE TABLE MarketTrends (
    trend_id INT PRIMARY KEY,
    trend_name VARCHAR(100),
    trend_description TEXT
);

CREATE TABLE products (
	product_id int,
	product_name varchar(255),
	category varchar(50),
	price decimal(10,2)
);

CREATE TABLE sales (
	sale_id int,
	product_id int,
	sale_date date,
	sale_amount decimal(10,2)
);

CREATE TABLE forecasts (
	forecast_id int,
	product_id int,
	forecast_date date,
	forecast_amount decimal(10,2)
);

CREATE TABLE market_trends (
	trend_id int,
	trend_category varchar(50),
	trend_description text
);

CREATE TABLE market_trends (
    trend_id SERIAL PRIMARY KEY,
    trend_date DATE,
    trend_description TEXT
);

CREATE TABLE demand_forecast (
    forecast_id SERIAL PRIMARY KEY,
    product_id INT REFERENCES product_catalog(product_id),
    forecast_date DATE,
    predicted_demand INT
);

CREATE TABLE customer_feedback (
    feedback_id SERIAL PRIMARY KEY,
    product_id INT REFERENCES product_catalog(product_id),
    feedback_date DATE,
    feedback_text TEXT
);
