tables.table_schema:
CREATE TABLE ShoppingCartAbandonmentRates (
    user_id INT,
    cart_id INT,
    abandon_timestamp TIMESTAMP
);

CREATE TABLE ShoppingCartItems (
    item_id INT,
    cart_id INT,
    item_name VARCHAR(255),
    quantity INT
);

CREATE TABLE product ( 
	product_id UUID PRIMARY KEY, 
	product_name VARCHAR, 
	category VARCHAR, 
	price DECIMAL(10, 2) 
);

CREATE TABLE abandonment_rate ( 
	abandonment_id UUID PRIMARY KEY, 
	customer_id UUID, 
	abandoned_product_id UUID, 
	abandonment_time TIMESTAMP 
);
