tables.table_schema:
CREATE TABLE energy_consumption (
    store_id INT PRIMARY KEY,
    store_name VARCHAR(255),
    address VARCHAR(255),
    energy_usage DECIMAL
);

CREATE TABLE EfficiencyImprovements (
	improvement_id INT PRIMARY KEY,
	store_id INT,
	improvement_description TEXT,
	improvement_cost DECIMAL
);

CREATE TABLE stores (
  store_id INT PRIMARY KEY,
  store_name VARCHAR(255) NOT NULL,
  location VARCHAR(255) NOT NULL
);

CREATE TABLE efficiency_opportunities (
  opportunity_id INT PRIMARY KEY,
  store_id INT NOT NULL,
  opportunity_description TEXT NOT NULL,
  CONSTRAINT fk_store FOREIGN KEY (store_id) REFERENCES stores(store_id)
);

CREATE TABLE audit_log (
  log_id INT PRIMARY KEY,
  event_time TIMESTAMP NOT NULL,
  event_description TEXT NOT NULL,
  user_id INT NOT NULL
);

CREATE TABLE Store (
    store_id INT PRIMARY KEY,
    store_name VARCHAR(255) NOT NULL,
    location VARCHAR(255) NOT NULL
);
