tables.table_schema:
CREATE TABLE sustainable_materials (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(255),
    material_used VARCHAR(255),
    sustainability_rating INT
);

CREATE TABLE products (
   product_id INT PRIMARY KEY,
   product_name VARCHAR(255),
   material_used VARCHAR(255),
   environmentally_friendly BOOLEAN
);

CREATE TABLE sustainability_standards (
   standard_id INT PRIMARY KEY,
   standard_name VARCHAR(255),
   criteria VARCHAR(255),
   description TEXT
);

CREATE TABLE products (
    product_id SERIAL PRIMARY KEY,
    name TEXT NOT NULL,
    material TEXT NOT NULL
);

CREATE TABLE materials (
    material_id SERIAL PRIMARY KEY,
    name TEXT NOT NULL,
    sustainability_rating INT NOT NULL
);

CREATE TABLE materials (
    material_id INT PRIMARY KEY,
    material_name VARCHAR(50) NOT NULL,
    material_description TEXT
);

CREATE TABLE products (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(50) NOT NULL,
    product_description TEXT,
    material_id INT REFERENCES materials(material_id)
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(50) NOT NULL,
    customer_email VARCHAR(50) UNIQUE
);

CREATE TABLE product_reviews (
    review_id INT PRIMARY KEY,
    review_description TEXT,
    review_rating INT CHECK (review_rating >= 1 AND review_rating <= 5),
    product_id INT REFERENCES products(product_id),
    customer_id INT REFERENCES customers(customer_id)
);

CREATE TABLE Materials (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    description TEXT,
    sustainable BOOLEAN
);

CREATE TABLE Suppliers (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    contact_info TEXT,
    rating INT
);

CREATE TABLE ProductMaterials (
    product_id INT,
    material_id INT,
    quantity INT
);

CREATE TABLE Certifications (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    description TEXT,
    valid_until DATE
);
