tables.table_schema:
CREATE TABLE waste_collection (
  id INT PRIMARY KEY,
  waste_type VARCHAR(100),
  collection_date DATE,
  collection_point VARCHAR(100)
);

CREATE TABLE recycling_center (
  id INT PRIMARY KEY,
  center_name VARCHAR(100),
  location VARCHAR(100),
  capacity INT
);

CREATE TABLE WasteBins (
    id INT PRIMARY KEY,
    bin_location VARCHAR(255),
    capacity INT
);

CREATE TABLE RecyclableItems (
    item_id INT PRIMARY KEY,
    item_name VARCHAR(255),
    bin_id INT,
    is_recyclable BOOLEAN
);

CREATE TABLE WasteDisposalLogs (
    log_id INT PRIMARY KEY,
    bin_id INT,
    disposal_date TIMESTAMP,
    disposed_item_id INT
);

CREATE TABLE waste_collection_sites (
    id INT PRIMARY KEY,
    site_name VARCHAR(255),
    location VARCHAR(255),
    open_hours VARCHAR(100)
);

CREATE TABLE waste_types (
    type_id INT PRIMARY KEY,
    waste_type VARCHAR(255),
    recyclable BOOLEAN
);
