tables.table_schema:
CREATE TABLE supplier ( 
    id SERIAL PRIMARY KEY, 
    name VARCHAR(255) NOT NULL, 
    location VARCHAR(255) NOT NULL, 
    is_eco_friendly BOOLEAN 
);

CREATE TABLE product ( 
    id SERIAL PRIMARY KEY, 
    name VARCHAR(255) NOT NULL, 
    supplier_id INT REFERENCES supplier(id), 
    price DECIMAL(10, 2) NOT NULL, 
    is_sustainable BOOLEAN 
);

CREATE TABLE suppliers (
  supplier_id INT PRIMARY KEY,
  supplier_name VARCHAR(100),
  sustainability_rating INT
);

CREATE TABLE product_sources (
  product_id INT,
  source_name VARCHAR(100),
  sustainability_certification VARCHAR(50),
  FOREIGN KEY (product_id) REFERENCES products(product_id)
);

CREATE TABLE suppliers (
  supplier_id INT PRIMARY KEY,
  supplier_name VARCHAR(255) NOT NULL,
  supplier_location VARCHAR(255) NOT NULL,
  is_ecofriendly BOOLEAN
);

CREATE TABLE supplier_relationships (
  supplier_id INT references suppliers(supplier_id),
  related_supplier_id INT references suppliers(supplier_id),
  relationship_type VARCHAR(50),
  PRIMARY KEY (supplier_id, related_supplier_id)
);

CREATE TABLE suppliers (
  supplier_id INT PRIMARY KEY,
  supplier_name VARCHAR(255) NOT NULL,
  contact_person VARCHAR(255) NOT NULL,
  email VARCHAR(255) NOT NULL
);

CREATE TABLE sustainability_info (
  product_id INT PRIMARY KEY,
  eco_friendly BOOLEAN NOT NULL,
  sustainable BOOLEAN NOT NULL
);
