tables.table_schema:
CREATE TABLE packaging_materials (
    material_id SERIAL PRIMARY KEY,
    material_name VARCHAR(50) NOT NULL,
    material_type VARCHAR(50) NOT NULL,
    is_renewable BOOLEAN
);

CREATE TABLE packaging_options (
    option_id SERIAL PRIMARY KEY,
    option_name VARCHAR(50) NOT NULL,
    material_id INT REFERENCES packaging_materials(material_id),
    cost DECIMAL(10, 2) NOT NULL
);

CREATE TABLE PackagingMaterials (
  material_id UUID PRIMARY KEY,
  material_name STRING,
  material_type STRING,
  eco_friendly BOOLEAN
);

CREATE TABLE PackagingDesigns (
  design_id UUID PRIMARY KEY,
  design_name STRING,
  design_color STRING,
  design_pattern STRING
);

CREATE TABLE Product (
  id INT PRIMARY KEY,
  name VARCHAR(255),
  description TEXT
);

CREATE TABLE Packaging (
  id INT PRIMARY KEY,
  material VARCHAR(255),
  size VARCHAR(50),
  weight DECIMAL
);

CREATE TABLE Supplier (
  id INT PRIMARY KEY,
  name VARCHAR(100),
  location VARCHAR(255)
);

CREATE TABLE Product (
  product_id INT PRIMARY KEY,
  product_name VARCHAR(255) NOT NULL,
  product_category VARCHAR(50) NOT NULL,
  package_material VARCHAR(50) NOT NULL
);

CREATE TABLE Supplier (
  supplier_id INT PRIMARY KEY,
  supplier_name VARCHAR(255) NOT NULL,
  supplier_location VARCHAR(100) NOT NULL
);

CREATE TABLE PackagingMaterial (
  material_id INT PRIMARY KEY,
  material_name VARCHAR(50) NOT NULL
);

CREATE TABLE Package (
  package_id INT PRIMARY KEY,
  package_size FLOAT NOT NULL,
  package_weight FLOAT NOT NULL,
  material_id INT REFERENCES PackagingMaterial(material_id)
);

CREATE TABLE ProductPackage (
  product_id INT REFERENCES Product(product_id),
  package_id INT REFERENCES Package(package_id),
  PRIMARY KEY (product_id, package_id)
);
