tables.table_schema:
CREATE TABLE customer_geo_data (
  customer_id INT PRIMARY KEY,
  customer_name VARCHAR(255),
  customer_location GEOGRAPHY
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(50),
    location geography
);

CREATE TABLE stores (
    store_id INT PRIMARY KEY,
    store_name VARCHAR(50),
    location geography
);

CREATE TABLE offers (
  id UUID PRIMARY KEY,
  name STRING,
  location GEOGRAPHY
);

CREATE TABLE customers (
  id UUID PRIMARY KEY,
  name STRING,
  email STRING,
  location GEOGRAPHY
);

CREATE TABLE store_recommendations (
    recommendation_id SERIAL PRIMARY KEY,
    store_name VARCHAR(100),
    location_id INT REFERENCES locations(location_id),
    recommendation_score FLOAT
);

CREATE TABLE Customers (
    customer_id INT PRIMARY KEY,
    name TEXT,
    email TEXT,
    location GEOGRAPHY
);

CREATE TABLE Stores (
    store_id INT PRIMARY KEY,
    name TEXT,
    location GEOGRAPHY,
    rating FLOAT
);

CREATE TABLE Customer_Interests (
    customer_id INT,
    interest TEXT,
    PRIMARY KEY (customer_id, interest)
);

CREATE TABLE Offer_Stores (
    offer_id INT,
    store_id INT,
    PRIMARY KEY (offer_id, store_id)
);
