tables.table_schema:
CREATE TABLE inventory (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(255) NOT NULL,
    current_stock INT NOT NULL,
    sold_units INT NOT NULL
);

CREATE TABLE sales (
    sale_id INT PRIMARY KEY,
    product_id INT REFERENCES inventory(product_id),
    sale_date DATE NOT NULL,
    sale_amount DECIMAL(10, 2) NOT NULL
);

CREATE TABLE replenish (
    replenish_id INT PRIMARY KEY,
    product_id INT REFERENCES inventory(product_id),
    replenish_date DATE NOT NULL,
    replenish_quantity INT NOT NULL
);

CREATE TABLE inventory_levels (
   product_id INT PRIMARY KEY,
   quantity INT
);

CREATE TABLE sales_data (
   sale_id INT PRIMARY KEY,
   product_id INT,
   sale_date DATE,
   sale_quantity INT
);

CREATE TABLE stock_replenishment (
   replenishment_id INT PRIMARY KEY,
   product_id INT,
   replenishment_date DATE,
   replenishment_quantity INT
);

CREATE TABLE automatic_replenishment (
   replenishment_id INT PRIMARY KEY,
   product_id INT,
   next_replenishment_date DATE
);

CREATE TABLE Inventory (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(255),
    current_stock INT,
    minimum_stock INT
);

CREATE TABLE Replenishment (
    replenishment_id INT PRIMARY KEY,
    product_id INT,
    replenishment_date DATE,
    replenishment_quantity INT
);

CREATE TABLE Replenishment_Trigger (
    trigger_id INT PRIMARY KEY,
    product_id INT,
    trigger_type VARCHAR(50)
);

CREATE TABLE Replenishment_Log (
    log_id INT PRIMARY KEY,
    replenishment_id INT,
    log_date DATE,
    log_message TEXT
);
