tables.table_schema:
CREATE TABLE loyalty_program (
    program_id INT PRIMARY KEY,
    program_name VARCHAR(50) NOT NULL,
    shopper_id INT NOT NULL,
    points_balance INT DEFAULT 0
);

CREATE TABLE customers (
  customer_id INT PRIMARY KEY,
  name VARCHAR(50) NOT NULL,
  email VARCHAR(100) UNIQUE,
  points INT DEFAULT 0
);

CREATE TABLE loyalty_programs (
    loyalty_program_id INT PRIMARY KEY,
    program_name VARCHAR(255),
    rewards_description TEXT
);

CREATE TABLE purchases (
    purchase_id INT PRIMARY KEY,
    shopper_id INT REFERENCES shoppers(shopper_id),
    purchase_date DATE NOT NULL,
    total_amount DECIMAL(10, 2) NOT NULL
);

CREATE TABLE rewards (
    reward_id INT PRIMARY KEY,
    shopper_id INT REFERENCES shoppers(shopper_id),
    reward_date DATE NOT NULL,
    reward_description TEXT
);

CREATE TABLE loyalty_program (
    loyalty_id UUID PRIMARY KEY,
    customer_id UUID NOT NULL,
    points INT NOT NULL,
    join_date DATE
);

CREATE TABLE customer (
    customer_id UUID PRIMARY KEY,
    first_name VARCHAR(50) NOT NULL,
    last_name VARCHAR(50) NOT NULL,
    email VARCHAR(100) UNIQUE
);

CREATE TABLE transaction (
    transaction_id UUID PRIMARY KEY,
    customer_id UUID NOT NULL,
    transaction_date DATE,
    total_amount DECIMAL(10, 2) NOT NULL
);

CREATE TABLE rewards (
    reward_id UUID PRIMARY KEY,
    loyalty_id UUID NOT NULL,
    reward_type VARCHAR(50) NOT NULL,
    reward_value DECIMAL(5, 2) NOT NULL
);

CREATE TABLE redemption (
    redemption_id UUID PRIMARY KEY,
    reward_id UUID NOT NULL,
    redeemed_date DATE,
    redeemed_points INT NOT NULL
);
