tables.table_schema:
CREATE TABLE demand_data (
   demand_id INT PRIMARY KEY,
   strategy_id INT,
   date DATE,
   demand_value INT
);

CREATE TABLE products(
    product_id INT PRIMARY KEY,
    product_name VARCHAR(50),
    category VARCHAR(20),
    manufacturer VARCHAR(50)
);

CREATE TABLE pricing_strategy ( 
    strategy_id SERIAL PRIMARY KEY, 
    strategy_name TEXT NOT NULL, 
    min_price FLOAT NOT NULL, 
    max_price FLOAT NOT NULL 
);

CREATE TABLE demand_analysis ( 
    demand_id SERIAL PRIMARY KEY, 
    demand_name TEXT NOT NULL, 
    demand_level INT NOT NULL, 
    demand_date DATE NOT NULL 
);

CREATE TABLE competition_analysis ( 
    competition_id SERIAL PRIMARY KEY, 
    competition_name TEXT NOT NULL, 
    competition_level INT NOT NULL, 
    competition_date DATE NOT NULL 
);

CREATE TABLE pricing_recommendation ( 
    recommendation_id SERIAL PRIMARY KEY, 
    strategy_id INT NOT NULL, 
    recommended_price FLOAT NOT NULL, 
    recommendation_date DATE NOT NULL 
);
