);

CREATE TABLE adverse_effects (
  effect_id INT PRIMARY KEY,
  effect_name VARCHAR(255) NOT NULL,
  effect_description TEXT
);

CREATE TABLE Drug_Interactions (
	interaction_id INT PRIMARY KEY,
	drug_name VARCHAR(50) NOT NULL,
	interaction_type VARCHAR(50),
	adverse_effect VARCHAR(100)
);

CREATE TABLE Patients (
	patient_id INT PRIMARY KEY,
	patient_name VARCHAR(50) NOT NULL,
	age INT,
	gender VARCHAR(10)
);

CREATE TABLE Drug_Usage (
	usage_id INT PRIMARY KEY,
	patient_id INT,
	drug_name VARCHAR(50) NOT NULL,
	dosage VARCHAR(20)
);

CREATE TABLE Adverse_Effects (
	effect_id INT PRIMARY KEY,
	drug_name VARCHAR(50) NOT NULL,
	effect_description VARCHAR(100)
);

CREATE TABLE DrugInteractions (
  drug_id INT PRIMARY KEY,
  drug_name VARCHAR(255),
  interaction_id INT,
  interaction_description TEXT
);

CREATE TABLE AdverseEffects (
  effect_id INT PRIMARY KEY,
  effect_name VARCHAR(255),
  severity_level VARCHAR(50),
  is_serious BOOLEAN
);
